import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, onAuthStateChanged, signInWithPopup, GoogleAuthProvider } from 'firebase/auth';
import axios from 'axios';

import './App.css';

function App() {
  const [userId, setUserId] = useState(null);
  const [isSignedIn, setIsSignedIn] = useState(false);

  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [year, setYear] = useState('');
  const [searchAuthor, setSearchAuthor] = useState('');

  const firebaseConfig = {
    apiKey: "AIzaSyDA5fETL0mQ5LspBpywgCqdUFCKbLzrfkk",
    authDomain: "filtered-search.firebaseapp.com",
    projectId: "filtered-search",
    storageBucket: "filtered-search.appspot.com",
    messagingSenderId: "579002251149",
    appId: "1:579002251149:web:8d2feccf7ef71957952fc4",
    measurementId: "G-JCRMNNWZC4"
  };

  initializeApp(firebaseConfig);

  useEffect(() => {
    const auth = getAuth();

    const unsubscribe = onAuthStateChanged(auth, (user) => {
      console.log('Auth state changed:', user);

      if (user) {
        // User is signed in
        console.log('User is signed in:', user);
        setUserId(user.displayName);
        setIsSignedIn(true);
        // Fetch books when the user is signed in
        displayAllBooks();
      } else {
        // No user is signed in
        console.log('No user is signed in.');
        setUserId(null);
        setIsSignedIn(false);
        // Fetch books when the user is signed in
      }
    });

    return () => unsubscribe();
  }, []);


  const signInWithGoogle = () => {
    const provider = new GoogleAuthProvider();
    const auth = getAuth();
    signInWithPopup(auth, provider)
      .then((result) => {
        // User signed in
        console.log("Logged In", result);
      })
      .catch((error) => {
        // Handle Errors here.
        console.error(error);
      });
  };

  const displayAllBooks = async () => {
    try {
      const response = await axios.get('https://nth-guide-404519.uw.r.appspot.com/findAllBooks');
      setBooks(response.data);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  const handleAuthorSearch = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.get(`https://nth-guide-404519.uw.r.appspot.com/findBooksByAuthor?author=${searchAuthor}`);
      setBooks(response.data);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const postData = {
      title,
      author,
      year: parseInt(year, 10),
    };

    try {
      const response = await axios.post('https://nth-guide-404519.uw.r.appspot.com/saveBook', postData);
      console.log('Response:', response.data);
      // After posting a new book, refresh the list of all books
      displayAllBooks();
    } catch (error) {
      console.error('Error posting data:', error);
    }
  };

  const signOut = async () => {
    const auth = getAuth();
    try {
      await auth.signOut();
      setIsSignedIn(false);
      setUserId(null);
      setBooks([]); // Clear the books when the user signs out
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  return (
    <div>
      {isSignedIn ? (
        <div>
          {/* User is signed in, display App 1 (BooksComponent) functionality */}
          <h1>Welcome, User {userId}</h1>

          {/* Form for searching by author */}
          <form onSubmit={handleAuthorSearch}>
            <label>
              Search by Author:
              <input type="text" value={searchAuthor} onChange={(e) => setSearchAuthor(e.target.value)} />
            </label>
            <button type="submit">Search</button>
          </form>

          {/* Form for posting a new book */}
          <form onSubmit={handleSubmit}>
            <label>
              Title:
              <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
            </label>
            <br />
            <label>
              Author:
              <input type="text" value={author} onChange={(e) => setAuthor(e.target.value)} />
            </label>
            <br />
            <label>
              Year:
              <input type="number" value={year} onChange={(e) => setYear(e.target.value)} />
            </label>
            <br />
            <button type="submit">Submit</button>
          </form>

          {/* Display list of books */}
          {books.map((book, index) => (
            <div key={index} className="book-item">
              <h3>{book.title}</h3>
              <p>by {book.author}</p>
            </div>
          ))}

          {/* Sign-out button */}
          <button onClick={signOut}>Sign Out</button>
        </div>
      ) : (
        <div>
          {/* User is not signed in, display App 2 functionality (sign in with Google) */}
          <button className="sign-in-button" onClick={signInWithGoogle}>
            Sign in with Google
          </button>
          <p className="welcome-message">Welcome!</p>
        </div>
      )}
    </div>
  );
}

export default App;
