import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithRedirect, GoogleAuthProvider } from 'firebase/auth';
import './BookList.css';

function BooksComponent() {
  // Existing state variables
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [id, setId] = useState('');
  const [year, setYear] = useState('');
  const [searchAuthor, setSearchAuthor] = useState('');

  // State variables from the new code
  const [userId, setUserId] = useState('');

  // Function to handle the author search form submission
  const handleAuthorSearch = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.get(`https://nth-guide-404519.uw.r.appspot.com/findBooksByAuthor?author=${searchAuthor}`);
      setBooks(response.data);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  // Function to call API to get all books in DB
  const displayAllBooks = async () => {
    try {
      const response = await axios.get('https://nth-guide-404519.uw.r.appspot.com/findAllBooks');
      setBooks(response.data);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  // Function to handle the user submit of a new book
  const handleSubmit = async (event) => {
    event.preventDefault();

    const postData = {
      title,
      author,
      year: parseInt(year, 10),
    };

    try {
      const response = await axios.post('https://nth-guide-404519.uw.r.appspot.com/saveBook', postData);
      console.log('Response:', response.data);
      // After posting a new book, refresh the list of all books
      displayAllBooks();
    } catch (error) {
      console.error('Error posting data:', error);
    }
  };

  // useEffect for fetching data when component mounts
  useEffect(() => {
    // Using Axios to fetch data
    displayAllBooks();
  }, []);

  // Existing code from App component
  const firebaseConfig = {
    apiKey: "AIzaSyDA5fETL0mQ5LspBpywgCqdUFCKbLzrfkk",
    authDomain: "filtered-search.firebaseapp.com",
    projectId: "filtered-search",
    storageBucket: "filtered-search.appspot.com",
    messagingSenderId: "579002251149",
    appId: "1:579002251149:web:8d2feccf7ef71957952fc4",
    measurementId: "G-JCRMNNWZC4"
  };

  initializeApp(firebaseConfig);

  const signInWithGoogle = () => {
    const provider = new GoogleAuthProvider();
    const auth = getAuth();
    signInWithRedirect(auth, provider)
      .then((result) => {
        // User signed in
        console.log(result.user);
      })
      .catch((error) => {
        // Handle Errors here.
        console.error(error);
      });
  };

  const auth = getAuth();
  auth.onAuthStateChanged((user) => {
    if (user) {
      // User is signed in.
      console.log('User is signed in:', user);
      setUserId(user.uid);
    } else {
      // No user is signed in.
      console.log('No user is signed in.');
    }
  });

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  // Combined render section
  return (
    <div className="book-list">
      {/* Form for searching by author */}
      <form onSubmit={handleAuthorSearch}>
        {/* ... (existing search form elements) */}
      </form>

      {/* Form for posting a new book */}
      <form onSubmit={handleSubmit}>
        {/* ... (existing form elements) */}
      </form>

      {/* Display list of books */}
      {books.map((book, index) => (
        <div key={index} className="book-item">
          <h3>{book.title}</h3>
          <p>by {book.author}</p>
        </div>
      ))}

      {/* Firebase authentication elements */}
      <div>
        <button onClick={signInWithGoogle}>Sign in with Google</button>
        <p>{userId}</p>
      </div>
    </div>
  );
}

export default BooksComponent;
