import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './BookList.css';

function BooksComponent() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [id, setId] = useState('');
  const [year, setYear] = useState('');
  const [searchAuthor, setSearchAuthor] = useState('');

  // Function to handle the author search form submission
  const handleAuthorSearch = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.get(`https://nth-guide-404519.uw.r.appspot.com/findBooksByAuthor?author=${searchAuthor}`);
      setBooks(response.data);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  // Function to call API to get all books in DB
  const displayAllBooks = async () => {
    try {
      const response = await axios.get('https://nth-guide-404519.uw.r.appspot.com/findAllBooks');
      setBooks(response.data);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  // Function to handle the user submit of a new book
  const handleSubmit = async (event) => {
    event.preventDefault();

    const postData = {
      title,
      author,
      year: parseInt(year, 10), // Convert string to integer
    };

    try {
      const response = await axios.post('https://nth-guide-404519.uw.r.appspot.com/saveBook', postData);
      console.log('Response:', response.data);
      // After posting a new book, refresh the list of all books
      displayAllBooks();
    } catch (error) {
      console.error('Error posting data:', error);
    }
  };

  // useEffect makes it so the list of books is shown when this component mounts
  useEffect(() => {
    // Using Axios to fetch data
    displayAllBooks();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  // This component displays a list of books and has forms for posting a new book and searching by author
  return (
    <div className="book-list">
      {/* Form for searching by author */}
      <form onSubmit={handleAuthorSearch}>
        <label>
          Search by Author:
          <input
            type="text"
            value={searchAuthor}
            onChange={(e) => setSearchAuthor(e.target.value)}
          />
        </label>
        <button type="submit">Search</button>
      </form>

      {/* Form for posting a new book */}
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
        </label>
        <br />
        <label>
          Author:
          <input type="text" value={author} onChange={(e) => setAuthor(e.target.value)} />
        </label>
        <br />

        <label>
          Year:
          <input type="number" value={year} onChange={(e) => setYear(e.target.value)} />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>

      {/* Display list of books */}
      {books.map((book, index) => (
        <div key={index} className="book-item">
          <h3>{book.title}</h3>
          <p>by {book.author}</p>
        </div>
      ))}
    </div>
  );
}

export default BooksComponent;
